const inquirer = require('inquirer');
let open = require("open");
const systemconfig = require('../config/system')
let port = systemconfig.port

class answer {
  chooseurl() {
    const promptList = [{
      type: 'list',
      message: '请选择抓取的网站:',
      name: 'site',
      choices: [
        "头条",
        "simg",
        "微博",
        "百度"
      ]
    }];

    inquirer.prompt(promptList).then(a => {
      switch (a.site) {
        case '头条':
          open(`http://localhost:${port}/bigdata/toutiao/`)
          break;
        case 'simg':
          open(`http://localhost:${port}/bigdata/simg`)
          break;
        case '微博':
          open(`http://localhost:${port}/bigdata/`)
          break;
        case '百度':
          open(`http://localhost:${port}/bigdata/`)
          break;
      }
    })
  }
}

module.exports = answer